// Coupons: code => discount percent
const coupons = {
  SAVE10: 10,
  WELCOME15: 15,
  FALL20: 20,
};

// State
let cart = JSON.parse(localStorage.getItem('cart')) || {};
let wishlist = JSON.parse(localStorage.getItem('wishlist')) || {};
let appliedCoupon = null;

// Cache DOM elements
const views = {
  home: document.getElementById('view-home'),
  wishlist: document.getElementById('view-wishlist'),
  cart: document.getElementById('view-cart'),
  checkout: document.getElementById('view-checkout'),
};
const navButtons = document.querySelectorAll('.nav-btn');
const productsContainer = document.getElementById('products');
const wishlistContainer = document.getElementById('wishlist-products');
const wishlistEmptyMsg = document.getElementById('wishlist-empty-msg');
const wishlistCountEl = document.getElementById('wishlist-count');
const cartCountEl = document.getElementById('cart-count');
const cartItemsContainer = document.getElementById('cart-items');
const couponInput = document.getElementById('coupon-input');
const applyCouponBtn = document.getElementById('apply-coupon-btn');
const couponMsg = document.getElementById('coupon-msg');
const cartSummary = document.getElementById('cart-summary');
const goToCheckoutBtn = document.getElementById('go-to-checkout-btn');
const searchInput = document.getElementById('search-input');
const categoryFilter = document.getElementById('category-filter');
const sortSelect = document.getElementById('sort-select');
const checkoutForm = document.getElementById('checkout-form');
const orderSuccessMsg = document.getElementById('order-success');
const backToHomeBtn = document.getElementById('back-to-home');

// Util: Render stars for rating (out of 5)
function renderStars(rating) {
  const fullStars = Math.floor(rating);
  const halfStar = rating - fullStars >= 0.5;
  let starsHTML = '';
  for (let i = 0; i < fullStars; i++) starsHTML += '★';
  if (halfStar) starsHTML += '½';
  while (starsHTML.length < 5) starsHTML += '☆';
  // Alternative: better star icons can be used with SVG or fonts
  return starsHTML;
}

// Show only one view
function showView(view) {
  Object.values(views).forEach(v => v.classList.remove('active'));
  views[view].classList.add('active');
}

// Save cart & wishlist to localStorage
function saveState() {
  localStorage.setItem('cart', JSON.stringify(cart));
  localStorage.setItem('wishlist', JSON.stringify(wishlist));
}

// Update nav counts
function updateNavCounts() {
  wishlistCountEl.textContent = Object.keys(wishlist).length;
  cartCountEl.textContent = Object.values(cart).reduce((sum, qty) => sum + qty, 0);
}

// Render product cards (Home & Wishlist use same function, with buttons toggled)
function renderProductsList(container, productsList, showAddToCart = true, showWishlistBtn = true) {
  container.innerHTML = '';
  if (productsList.length === 0) {
    container.innerHTML = `<p class="empty-msg">No products found.</p>`;
    return;
  }

  for (const p of productsList) {
    const card = document.createElement('article');
    card.className = 'product-card';
    card.tabIndex = 0;
    card.setAttribute('role', 'article');
    card.innerHTML = `
      <img src="${p.image}" alt="${p.name}" loading="lazy" />
      <div class="product-info">
        <h3 class="product-name">${p.name}</h3>
        <div class="rating" aria-label="Rating: ${p.rating} out of 5 stars">${renderStars(p.rating)}</div>
        <p class="product-price">${p.price.toFixed(2)}</p>
        <p class="product-description">${p.description}</p>
        <button class="buy-button" ${showAddToCart ? '' : 'style="display:none"'} data-id="${p._id}" aria-label="Add ${p.name} to cart">Add to Cart</button>
        <button class="wishlist-button ${wishlist[p._id] ? 'active' : ''}" ${showWishlistBtn ? '' : 'style="display:none"'} data-id="${p._id}" aria-label="${wishlist[p._id] ? 'Remove' : 'Add'} ${p.name} ${wishlist[p._id] ? 'from' : 'to'} wishlist">
          ${wishlist[p._id] ? 'Remove from Wishlist' : 'Add to Wishlist'}
        </button>
      </div>
    `;
    container.appendChild(card);
  }
}

// Filter and sort products according to search, category, sort
function getFilteredSortedProducts() {
  let filtered = products;

  // Search
  const searchTerm = searchInput.value.trim().toLowerCase();
  if (searchTerm) {
    filtered = filtered.filter(p =>
      p.name.toLowerCase().includes(searchTerm) ||
      p.description.toLowerCase().includes(searchTerm)
    );
  }

  // Category filter
  const category = categoryFilter.value;
  if (category) {
    filtered = filtered.filter(p => p.category === category);
  }

  // Sort
  switch (sortSelect.value) {
    case 'price-asc':
      filtered = filtered.slice().sort((a, b) => a.price - b.price);
      break;
    case 'price-desc':
      filtered = filtered.slice().sort((a, b) => b.price - a.price);
      break;
    case 'rating-desc':
      filtered = filtered.slice().sort((a, b) => b.rating - a.rating);
      break;
    default:
      // no sort or default ordering
      break;
  }

  return filtered;
}

// Populate category filter options dynamically
function populateCategoryFilter() {
  const categories = [...new Set(products.map(p => p.category))];
  categoryFilter.innerHTML = `<option value="">All Categories</option>`;
  categories.forEach(cat => {
    const option = document.createElement('option');
    option.value = cat;
    option.textContent = cat;
    categoryFilter.appendChild(option);
  });
}

// Add item to cart
function addToCart(productId) {
  if (!cart[productId]) cart[productId] = 0;
  cart[productId]++;
  saveState();
  updateNavCounts();
  alert('Added to cart!');
}

// Remove item from cart
function removeFromCart(productId) {
  delete cart[productId];
  saveState();
  updateNavCounts();
  renderCartPage();
}

// Change quantity in cart
function changeCartQuantity(productId, qty) {
  if (qty < 1) {
    removeFromCart(productId);
  } else {
    cart[productId] = qty;
    saveState();
    updateNavCounts();
    renderCartPage();
  }
}

// Toggle wishlist
function toggleWishlist(productId) {
  if (wishlist[productId]) {
    delete wishlist[productId];
  } else {
    wishlist[productId] = true;
  }
  saveState();
  updateNavCounts();
  renderHomePage();
  renderWishlistPage();
}

// Render Wishlist page
function renderWishlistPage() {
  const wishProducts = products.filter(p => wishlist[p._id]);
  if (wishProducts.length === 0) {
    wishlistEmptyMsg.style.display = 'block';
  } else {
    wishlistEmptyMsg.style.display = 'none';
  }
  renderProductsList(wishlistContainer, wishProducts, true, true);
}

// Render Home page
function renderHomePage() {
  const filteredProducts = getFilteredSortedProducts();
  renderProductsList(productsContainer, filteredProducts, true, true);
}

// Render Cart page
function renderCartPage() {
  if (Object.keys(cart).length === 0) {
    cartItemsContainer.innerHTML = `<p class="empty-msg">Your cart is empty.</p>`;
    cartSummary.textContent = '';
    couponMsg.textContent = '';
    couponInput.value = '';
    appliedCoupon = null;
    return;
  }

  let html = `<table aria-label="Shopping cart items">
    <thead>
      <tr>
        <th>Product</th>
        <th>Price</th>
        <th>Quantity</th>
        <th>Subtotal</th>
        <th>Remove</th>
      </tr>
    </thead>
    <tbody>`;

  let total = 0;
  for (const [pid, qty] of Object.entries(cart)) {
    const p = products.find(prod => prod._id === pid);
    if (!p) continue;
    const subtotal = p.price * qty;
    total += subtotal;

    html += `
      <tr>
        <td>${p.name}</td>
        <td>${p.price.toFixed(2)}</td>
        <td>
          <div class="cart-qty-wrapper" role="group" aria-label="Quantity for ${p.name}">
            <button class="qty-btn" data-id="${pid}" data-action="dec" aria-label="Decrease quantity">−</button>
            <input type="number" class="cart-qty-input" data-id="${pid}" min="1" value="${qty}" aria-live="polite" aria-label="Quantity input" />
            <button class="qty-btn" data-id="${pid}" data-action="inc" aria-label="Increase quantity">+</button>
          </div>
        </td>
        <td>${subtotal.toFixed(2)}</td>
        <td><button class="remove-btn" data-id="${pid}" aria-label="Remove ${p.name} from cart">×</button></td>
      </tr>`;
  }
  html += '</tbody></table>';
  cartItemsContainer.innerHTML = html;

  // Apply coupon discount if any
  let discountAmount = 0;
  if (appliedCoupon && coupons[appliedCoupon]) {
    discountAmount = (total * coupons[appliedCoupon]) / 100;
  }
  const finalTotal = total - discountAmount;

  cartSummary.innerHTML = `
    <p>Total: ${total.toFixed(2)}</p>
    ${appliedCoupon ? `<p>Discount (${appliedCoupon}): -$${discountAmount.toFixed(2)}</p>` : ''}
    <p><strong>Final Total: ${finalTotal.toFixed(2)}</strong></p>
  `;

  couponMsg.textContent = '';
}

// Apply coupon
function applyCoupon() {
  const code = couponInput.value.trim().toUpperCase();
  if (!code) {
    couponMsg.textContent = 'Please enter a coupon code.';
    return;
  }
  if (!coupons[code]) {
    couponMsg.textContent = 'Invalid coupon code.';
    return;
  }
  appliedCoupon = code;
  couponMsg.textContent = `Coupon "${code}" applied!`;
  renderCartPage();
}

// Checkout form validation & submission
function handleCheckout(e) {
  e.preventDefault();

  // Simple validation
  const name = checkoutForm.name.value.trim();
  const email = checkoutForm.email.value.trim();
  const address = checkoutForm.address.value.trim();

  if (!name || !email || !address) {
    alert('Please fill out all required fields.');
    return;
  }
  if (!validateEmail(email)) {
    alert('Please enter a valid email address.');
    return;
  }

  // Simulate order success
  cart = {};
  appliedCoupon = null;
  saveState();
  updateNavCounts();
  checkoutForm.reset();
  orderSuccessMsg.style.display = 'block';
  showView('checkout');
}

// Email validation helper
function validateEmail(email) {
  return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
}

// Setup event listeners
function setupEventListeners() {
  // Navigation buttons
  navButtons.forEach(btn => {
    btn.addEventListener('click', () => {
      const target = btn.dataset.view;
      orderSuccessMsg.style.display = 'none';
      if (target === 'home') {
        renderHomePage();
      } else if (target === 'wishlist') {
        renderWishlistPage();
      } else if (target === 'cart') {
        renderCartPage();
      }
      showView(target);
    });
  });

  // Products container (delegation)
  productsContainer.addEventListener('click', e => {
    if (e.target.matches('.buy-button')) {
      addToCart(e.target.dataset.id);
    } else if (e.target.matches('.wishlist-button')) {
      toggleWishlist(e.target.dataset.id);
    }
  });

  // Wishlist container
  wishlistContainer.addEventListener('click', e => {
    if (e.target.matches('.buy-button')) {
      addToCart(e.target.dataset.id);
    } else if (e.target.matches('.wishlist-button')) {
      toggleWishlist(e.target.dataset.id);
    }
  });

  // Cart quantity buttons & input
  cartItemsContainer.addEventListener('click', e => {
    if (e.target.matches('.qty-btn')) {
      const pid = e.target.dataset.id;
      const action = e.target.dataset.action;
      let qty = cart[pid] || 1;
      if (action === 'inc') qty++;
      else if (action === 'dec') qty--;
      changeCartQuantity(pid, qty);
    } else if (e.target.matches('.remove-btn')) {
      removeFromCart(e.target.dataset.id);
    }
  });
  cartItemsContainer.addEventListener('input', e => {
    if (e.target.matches('.cart-qty-input')) {
      const pid = e.target.dataset.id;
      let val = parseInt(e.target.value, 10);
      if (isNaN(val) || val < 1) val = 1;
      changeCartQuantity(pid, val);
    }
  });

  // Coupon apply
  applyCouponBtn.addEventListener('click', applyCoupon);

  // Go to checkout
  goToCheckoutBtn.addEventListener('click', () => {
    orderSuccessMsg.style.display = 'none';
    showView('checkout');
  });

  // Checkout form submit
  checkoutForm.addEventListener('submit', handleCheckout);

  // Back to home after order
  backToHomeBtn.addEventListener('click', () => {
    renderHomePage();
    showView('home');
    orderSuccessMsg.style.display = 'none';
  });

  // Search & filters
  searchInput.addEventListener('input', () => {
    renderHomePage();
  });
  categoryFilter.addEventListener('change', () => {
    renderHomePage();
  });
  sortSelect.addEventListener('change', () => {
    renderHomePage();
  });
}

// Initialize app
function init() {
  populateCategoryFilter();
  updateNavCounts();
  renderHomePage();
  showView('home');
  setupEventListeners();
}

init();